#----coding:utf-8-----import numpy as npimport xgboost as xgbimport pandas as pddef read_data(split):    data = pd.DataFrame(pd.read_csv('IO/one.csv'))    Y = data['A']    X = data.drop(['A'], axis=1)        index = np.arange(0, len(Y))    np.random.shuffle(index)    Y = Y.values[index]    X = X.values[index]        train_ = [X[:split], Y[:split]]    test_ = [X[split:], Y[split:]]    return train_, test_train_, test_ = read_data(1700)parameters = {    'booster' : 'gbtree',    'objective' : 'multi:softmax'}pl = list(parameters.items())num_rounds = 10model = xgb.train(pl, train_, num_rounds, early_stopping_rounds=100)pred = model.predict(test_, ntree_limit= model.best_ntree_limit)print(pred )